import React from 'react'
import "../style";

import {useSelector} from 'react-redux'
 function Burger() {
    const {burger, menu, total} = useSelector((state) => state.burgerRenduce);
    const renderBreadMid = () => { 
        return Object.entries(burger).map(([propsBurger,value],index) => {
            let breadMid = [];  
            for (let i = 0; i < value; i++) {
                breadMid.push(<div key={i} className={propsBuger}></div>);
              }
        })
        
        
      };
    return (
        <div className="container">
        <h3 className="display-4 text-success">Bài tập burger cybersoft</h3>
        <div className="row">
          <div className="col-7">
            <h3 className="text-center text-danger">Banh Burger cua ban</h3>
            <div className="breadTop"></div>
                {renderBreadMid}
            <div className="breadBottom"></div>
          </div>
          <div className="col-5">
            <h3 className="text-center text-success">Chon thuc an</h3>
            <table className="table">
              <thead>
                <tr>
                  <th>Thuc an</th>
                  <th></th>
                  <th>Don gia</th>
                  <th>Thanh tien</th>
                </tr>
                
              </thead>
              <tfoot>
                <tr>
                <td colSpan="2"></td>
                <td>Tong tien</td>
                <td>{this.props.total}</td>
                </tr>
                
              </tfoot>
            </table>
          </div>
        </div>
      </div>
    )
}


export default Burger
