import {useDispatch} from 'react-redux';
import {addBreadMid} from '../redux/actions/burgerAction'

export default function AddBurger(burger,menu) {
    const dispatch = useDispatch();
    
    
        {Object.entries(menu).map(([propsBurger, price], index) => {
            return (
              <tr key={index}>
                <td>{propsBurger}</td>
                <td>
                  <button onClick={() => {dispatch(addBreadMid(propsBurger,1))}}  className="btn btn-success mr-3">+</button>
                  {burger[propsMenu]}
                  <button onClick={() => {dispatch(addBreadMid(propsBurger -1))}} className="btn btn-danger ml-3 mr-3">-</button>
      
                </td>
                <td>{price}</td>
                <td>{burger[propsBurger] * price}</td>
      
              </tr>
            );
          });} 
    
    
}
